INSERT INTO hadith_title(id, title, `next`) VALUES(0, "Les actions ne valent que par leurs intentions", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(1, "L'Islam, la foi, et la bienfaisance", 0);
