INSERT INTO hadith_title(id, title, `next`) VALUES(3, "Les dernières œuvres sont déterminantes", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(4, "La nullité des innovations", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(5, "Le licite et l'illicite", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(6, "La religion est le bon conseil", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(7, "Le caractère sacré du Musulman", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(8, "La responsabilité dépend de la capacité", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(9, "Ne sont acceptées que les bonnes choses", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(10, "Le scrupule", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(11, "Se consacrer aux choses utiles", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(12, "La foi parfaite", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(13, "L'inviolabilité du sang du Musulman", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(14, "Les vertus islamiques", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(15, "La piété et la haute moralité", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(16, "L'aide d'Allah et Sa protection", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(17, "La pudeur", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(18, "Persévérer dans la voie", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(19, "La facilité de la religion", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(20, "Faire assaut de bonnes actions", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(21, "L'interdiction absolue de l'injustice", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(22, "Le sens large de l'aumône", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(23, "La diversité des bonnes œuvres", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(24, "La piété c‟est la haute moralité", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(25, "L'attachement à la Sunna", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(26, "La voie du Paradis", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(27, "Les droits d'Allah", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(28, "La vraie ascèse", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(29, "Tout préjudice est illégal", 0);
INSERT INTO hadith_title(id, title, `next`) VALUES(30, "La preuve incombe au demandeur", 0);
